import React, { useState } from 'react';
import '../styles/Photos.css';

function Photos() {
  const photos = [
    {
      title: 'Beta',
      platform: 'Mac',
      description: 'Be the first to test our latest features on Mac by downloading our beta app.',
      buttonText: 'Download',
      imageUrl: 'https://via.placeholder.com/150',
    },
    {
      title: 'Desktop',
      platform: 'Windows',
      description: 'Requires Windows systems, you can download the app from the Microsoft Store.',
      buttonText: 'Download from Microsoft Store',
      imageUrl: 'https://via.placeholder.com/150',
    },
    {
      title: 'Mobile',
      platform: 'Android',
      description: 'Download our app on Android devices to stay connected on the go.',
      buttonText: 'Download from Play Store',
      imageUrl: 'https://via.placeholder.com/150',
    },
  ];

  const [currentIndex, setCurrentIndex] = useState(0);

  const handleNext = () => {
    setCurrentIndex((prevIndex) => (prevIndex === photos.length - 1 ? 0 : prevIndex + 1));
  };

  const handlePrev = () => {
    setCurrentIndex((prevIndex) => (prevIndex === 0 ? photos.length - 1 : prevIndex - 1));
  };

  return (
    <div className="photos-container">
      {/* Heading and Subheading */}
      <div className="info-section">
        <h1>Other download options</h1>
        <p>Stay connected with friends and family across your devices.</p>
      </div>

      {/* Carousel Section */}
      <div className="carousel-container">
        <button className="carousel-arrow left-arrow" onClick={handlePrev}>
          &#8249;
        </button>

        <div className="photo-card">
          <h3>{photos[currentIndex].title}</h3>
          <h2>{photos[currentIndex].platform}</h2>
          <p>{photos[currentIndex].description}</p>
          <a href="#" className="download-link">{photos[currentIndex].buttonText} &#8250;</a>
        </div>

        <button className="carousel-arrow right-arrow" onClick={handleNext}>
          &#8250;
        </button>
      </div>
    </div>
  );
}

export default Photos;
