import React, { useState } from 'react';
import '../styles/Photos.css';

function Photos() {
  const photos = [
    {
      imageUrl: 'https://via.placeholder.com/400x400', // Placeholder image 1
    },
    {
      imageUrl: 'https://via.placeholder.com/300x200', // Placeholder image 2
    },
  ];

  const [currentIndex, setCurrentIndex] = useState(0);

  const handleNext = () => {
    setCurrentIndex((prevIndex) => (prevIndex === photos.length - 1 ? 0 : prevIndex + 1));
  };

  const handlePrev = () => {
    setCurrentIndex((prevIndex) => (prevIndex === 0 ? photos.length - 1 : prevIndex - 1));
  };

  return (
    <div className="photos-container">
      {/* Heading and Subheading */}
      <div className="info-section">
        <h1>Already downloaded?</h1>
        <p>Learn more about what you can do on WhatsApp.</p>
      </div>

      {/* Carousel Section */}
      <div className="carousel-container">
        <button className="carousel-arrow" onClick={handlePrev}>
          &#8249;
        </button>

        <div className="photo-card">
          <img src={photos[currentIndex].imageUrl} alt={`Image ${currentIndex + 1}`} className="card-image" />
        </div>

        <button className="carousel-arrow" onClick={handleNext}>
          &#8250;
        </button>
      </div>
    </div>
  );
}

export default Photos;
