import React, { useState } from 'react';
import '../styles/Photos.css';

function Photos() {
  const photos = [
    {
      title: 'iOS',
      description: 'Minimum Requirements (Version 22.13.7). Requires iOS 12.0 or newer.',
      imageUrl: 'https://via.placeholder.com/400x400', // Placeholder image
      storeLink: 'Download on the App Store'
    },
    {
      title: 'Android',
      description: 'Minimum Requirements (Version 22.13.7). Requires OS 4.0.3 or above.',
      imageUrl: 'https://via.placeholder.com/300x200', // Placeholder image
      storeLink: 'Get it on Google Play'
    },
  ];

  const [currentIndex, setCurrentIndex] = useState(0);

  const handleNext = () => {
    setCurrentIndex((prevIndex) => (prevIndex === photos.length - 1 ? 0 : prevIndex + 1));
  };

  const handlePrev = () => {
    setCurrentIndex((prevIndex) => (prevIndex === 0 ? photos.length - 1 : prevIndex - 1));
  };

  return (
    <div className="photos-container">
      {/* Heading and Subheading */}
      <div className="info-section">
        <h1>Other download options</h1>
        <p>Stay connected with friends and family across your devices.</p>
      </div>

      {/* Carousel Section */}
      <div className="carousel-container">
        <button className="carousel-arrow" onClick={handlePrev}>
          &#8249;
        </button>

        <div className="photo-card">
          <img src={photos[currentIndex].imageUrl} alt={photos[currentIndex].title} className="card-image" />
          <div className="card-text">{photos[currentIndex].title}</div>
          <div className="card-description">{photos[currentIndex].description}</div>
          <button>{photos[currentIndex].storeLink}</button>
        </div>

        <button className="carousel-arrow" onClick={handleNext}>
          &#8250;
        </button>
      </div>
    </div>
  );
}

export default Photos;
