import React, { useEffect, useRef, useState } from "react";
import './YourCSSFile.css'; // Adjust to your actual CSS file path

const YourComponent = () => {
  const [isFirstSectionVisible, setIsFirstSectionVisible] = useState(false);
  const firstSectionRef = useRef(null);

  // Handle scrolling animations
  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            setIsFirstSectionVisible(true);
          }
        });
      },
      { threshold: 0.3 }
    );

    if (firstSectionRef.current) {
      observer.observe(firstSectionRef.current);
    }

    return () => {
      if (firstSectionRef.current) {
        observer.unobserve(firstSectionRef.current);
      }
    };
  }, []);

  const handleRegisterClick = () => {
    window.location.href = '/register'; // Adjust to your register page link
  };

  return (
    <>
      {/* Main Content Section */}
      <div className="main-content">
        <video className="background-video" autoPlay loop muted>
          <source src="your-video.mp4" type="video/mp4" />
          Your browser does not support the video tag.
        </video>
        <div className="overlay-content">
          <h1>We Are United Youth Parliament</h1>
          <p>Experience leadership like never before. Step up and join us.</p>
          <button className="cta-button" onClick={handleRegisterClick}>
            Register Now
          </button>
        </div>
      </div>

      {/* Valorant Styled Section */}
      <div className={`new-section valorant-section ${isFirstSectionVisible ? 'animate-section' : ''}`} ref={firstSectionRef}>
        <div className="valorant-content">
          <h2>Lead. Inspire. Empower.</h2>
          <p>Become part of the youth movement that drives positive change. Whether you're into social reform, leadership, or making a global impact, there's a place for you here.</p>
          <button className="cta-button" onClick={handleRegisterClick}>Join Now</button>
        </div>
        <div className="valorant-video">
          <video className="valorant-background-video" autoPlay loop muted>
            <source src="another-video.mp4" type="video/mp4" />
            Your browser does not support the video tag.
          </video>
        </div>
      </div>

      {/* Latest News Section */}
      <div className="latest-news-section">
        <h2 className="section-title">Latest News</h2>
        <div className="news-cards-container">
          <div className="news-card">
            <img className="news-image" src="news1.jpg" alt="news1" />
            <div className="news-content">
              <div className="news-category">Youth Leadership</div>
              <h3 className="news-title">Empowering Tomorrow's Leaders</h3>
              <p className="news-description">
                Discover how we are helping young people become the next generation of change-makers.
              </p>
              <div className="news-button-container">
                <a href="#" className="news-button">Read More</a>
              </div>
            </div>
          </div>
          {/* Additional News Cards here */}
        </div>
      </div>
    </>
  );
};

export default YourComponent;
