import React, { useState } from 'react';
import '../styles/Photos.css';

function Photos() {
  const photos = [
    {
      imageUrl: 'https://via.placeholder.com/400x400', // Placeholder image 1
    },
    {
      imageUrl: 'https://via.placeholder.com/400x400', // Placeholder image 2
    },
    {
      imageUrl: 'https://via.placeholder.com/400x400', // Placeholder image 3
    },
    {
      imageUrl: 'https://via.placeholder.com/400x400', // Placeholder image 4
    },
    {
      imageUrl: 'https://via.placeholder.com/400x400', // Placeholder image 5
    },
    {
      imageUrl: 'https://via.placeholder.com/400x400', // Placeholder image 6
    },
  ];

  const [currentIndex, setCurrentIndex] = useState(0);

  const handleNext = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === photos.length - 3 ? 0 : prevIndex + 1
    );
  };

  const handlePrev = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? photos.length - 3 : prevIndex - 1
    );
  };

  const visiblePhotos = photos.slice(currentIndex, currentIndex + 3);

  return (
    <div className="photos-container">
      {/* Heading and Subheading */}
      <div className="info-section">
        <h1>Other download options</h1>
        <p>Stay connected with friends and family across your devices.</p>
      </div>

      {/* Carousel Section */}
      <div className="carousel-container">
        <button className="carousel-arrow" onClick={handlePrev}>
          &#8249;
        </button>

        <div className="photo-gallery">
          {visiblePhotos.map((photo, index) => (
            <div key={index} className="photo-card">
              <img
                src={photo.imageUrl}
                alt={`Image ${index + 1}`}
                className="card-image"
              />
            </div>
          ))}
        </div>

        <button className="carousel-arrow" onClick={handleNext}>
          &#8250;
        </button>
      </div>
    </div>
  );
}

export default Photos;
