import React, { useState } from 'react';
import '../styles/Photos.css';

function Photos() {
  const photos = [
    {
      imageUrl: 'https://via.placeholder.com/400x300', // Larger image placeholder
    },
    {
      imageUrl: 'https://via.placeholder.com/400x300',
    },
    {
      imageUrl: 'https://via.placeholder.com/400x300',
    },
  ];

  const [currentIndex, setCurrentIndex] = useState(0);

  const handleNext = () => {
    setCurrentIndex((prevIndex) => (prevIndex === photos.length - 1 ? 0 : prevIndex + 1));
  };

  const handlePrev = () => {
    setCurrentIndex((prevIndex) => (prevIndex === 0 ? photos.length - 1 : prevIndex - 1));
  };

  return (
    <div className="photos-container">
      {/* Heading and Subheading */}
      <div className="info-section">
        <h1>Other download options</h1>
        <p>Stay connected with friends and family across your devices.</p>
      </div>

      {/* Carousel Section */}
      <div className="carousel-container">
        <div className="carousel-controls">
          <button className="carousel-arrow" onClick={handlePrev}>
            &#8249;
          </button>
        </div>

        <div className="photo-display">
          <img 
            src={photos[currentIndex].imageUrl} 
            alt={`Image ${currentIndex + 1}`} 
            className="large-image" 
          />
          <img 
            src={photos[(currentIndex + 1) % photos.length].imageUrl} 
            alt={`Image ${((currentIndex + 1) % photos.length) + 1}`} 
            className="small-image" 
          />
        </div>

        <div className="carousel-controls">
          <button className="carousel-arrow" onClick={handleNext}>
            &#8250;
          </button>
        </div>
      </div>
    </div>
  );
}

export default Photos;
