import React, { useState } from 'react';
import '../styles/Photos.css';

function Photos() {
  // Array of photo URLs (you can replace with actual images later)
  const photos = [
    "https://via.placeholder.com/300x200.png?text=Photo+1",
    "https://via.placeholder.com/300x200.png?text=Photo+2",
    "https://via.placeholder.com/300x200.png?text=Photo+3",
    "https://via.placeholder.com/300x200.png?text=Photo+4",
  ];

  // State to manage current photo index
  const [currentIndex, setCurrentIndex] = useState(0);

  // Function to handle next button click
  const handleNext = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === photos.length - 1 ? 0 : prevIndex + 1
    );
  };

  // Function to handle previous button click
  const handlePrev = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? photos.length - 1 : prevIndex - 1
    );
  };

  return (
    <div className="photos-container">
      <h1>Other download options</h1>
      <p>Stay connected with friends and family across your devices.</p>

      <div className="carousel">
        <button className="carousel-arrow left-arrow" onClick={handlePrev}>
          &#8249;
        </button>

        <div className="photo-wrapper">
          <img src={photos[currentIndex]} alt={`Photo ${currentIndex + 1}`} className="carousel-photo" />
        </div>

        <button className="carousel-arrow right-arrow" onClick={handleNext}>
          &#8250;
        </button>
      </div>
    </div>
  );
}

export default Photos;
