import React, { useEffect, useRef, useState } from "react";
import "./MainContent.css";
import video1 from "../assets/valorant-background.mp4"; // Add your video paths
import video2 from "../assets/defy-limits.mp4";
import video3 from "../assets/join-the-fight.mp4";

const MainContent = () => {
  const section1Ref = useRef(null);
  const section2Ref = useRef(null);
  const section3Ref = useRef(null);

  const [section1Visible, setSection1Visible] = useState(false);
  const [section2Visible, setSection2Visible] = useState(false);
  const [section3Visible, setSection3Visible] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const section1Top = section1Ref.current.getBoundingClientRect().top;
      const section2Top = section2Ref.current.getBoundingClientRect().top;
      const section3Top = section3Ref.current.getBoundingClientRect().top;
      const windowHeight = window.innerHeight;

      if (section1Top < windowHeight * 0.75) setSection1Visible(true);
      if (section2Top < windowHeight * 0.75) setSection2Visible(true);
      if (section3Top < windowHeight * 0.75) setSection3Visible(true);
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <div>
      {/* Section 1 */}
      <section className={`new-section ${section1Visible ? "animate-section" : ""}`} ref={section1Ref}>
        <div className="valorant-content">
          <h1 className="section-title">We Are Valorant</h1>
          <p className="section-description">A 5v5 character-based tactical shooter from Riot Games.</p>
          <button className="cta-button">Learn More</button>
        </div>
        <div className="valorant-video">
          <video className="valorant-background-video" autoPlay loop muted>
            <source src={video1} type="video/mp4" />
          </video>
        </div>
      </section>

      {/* Section 2 */}
      <section className={`new-section ${section2Visible ? "animate-section" : ""}`} ref={section2Ref}>
        <div className="valorant-content">
          <h1 className="section-title">Defy the Limits</h1>
          <p className="section-description">Show your strategic skills and rise to the top.</p>
          <button className="cta-button">Start Playing</button>
        </div>
        <div className="valorant-video">
          <video className="valorant-background-video" autoPlay loop muted>
            <source src={video2} type="video/mp4" />
          </video>
        </div>
      </section>

      {/* Section 3 */}
      <section className={`new-section ${section3Visible ? "animate-section" : ""}`} ref={section3Ref}>
        <div className="valorant-content">
          <h1 className="section-title">Join the Fight</h1>
          <p className="section-description">Be part of a global community and make your mark.</p>
          <button className="cta-button">Join Now</button>
        </div>
        <div className="valorant-video">
          <video className="valorant-background-video" autoPlay loop muted>
            <source src={video3} type="video/mp4" />
          </video>
        </div>
      </section>
    </div>
  );
};

export default MainContent;
